import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

export const fetchSameType = createAsyncThunk(
    'category/book',
    async (category) => {
        const response = await axios.get(`http://localhost:5000/api/v1/books/?category_code=${category}`);
        console.log(response);
        return response
    }
)


const initialState = {
    books: [],
    isloading: false,
    isError: false,
}

export const SameTypeSlice = createSlice({
    name: 'SameType',
    initialState,
    reducers: {

    },
    extraReducers: (builder) => {
        builder.addCase(fetchSameType.pending, (state, action) => {
            state.isloading = true
            state.isError = false
        })
        builder.addCase(fetchSameType.fulfilled, (state, action) => {
            state.books = action
            state.isloading = false
            state.isError = false
        })
        builder.addCase(fetchSameType.rejected, (state, action) => {
            // Add user to the state array
            state.isloading = false
            state.isError = true
        })
    },
})

export default SameTypeSlice.reducer
