import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

const token = localStorage.getItem('auth')

export const fetchUser = createAsyncThunk(
    'user',
    async (user) => {
        const response = await axios.get('http://localhost:5000/api/v1/user',
            { headers: { "Authorization": `${user.length !== 0 ? user : token}` } })
        return response.data
    }
)

export const updateUser = createAsyncThunk(
    'update',
    async (user) => {
        console.log(user);
        console.log(token);
        const response = await axios.put('http://localhost:5000/api/v1/user',
            { headers: { "Authorization": `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6NiwibmFtZSI6ImFkbWluIiwiZW1haWwiOiJhZG1pbkBnbWFpbC5jb20iLCJyb2xlX2NvZGUiOiJSMSIsImFkZHJlc3MiOiJhZG1pblZOVmluaEJUIiwiaWF0IjoxNjgxMzcwMDA3LCJleHAiOjE2ODE4MDIwMDd9.irGcQ1mPJSH70Nxo9XvX3QZMZgRvdA4Iu65NUKfhIRA` } })
        console.log(response);
        return response
    }
)


const initialState = {
    user: [],
    isloading: false,
    isError: false,
}

export const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {

    },
    extraReducers: (builder) => {
        builder.addCase(fetchUser.pending, (state, action) => {
            state.isloading = true
            state.isError = false
        })
        builder.addCase(fetchUser.fulfilled, (state, action) => {
            state.user = action.payload
            state.isloading = false
            state.isError = false
        })
        builder.addCase(fetchUser.rejected, (state, action) => {
            // Add user to the state array
            state.isloading = false
            state.isError = true
        })
    },
})

export default userSlice.reducer
